mapper.select:
select id, money, year, month, employee_id, payout_id
from salary
where id = #{id,jdbcType=BIGINT}

SELECT s.id, s. YEAR, s. MONTH, e.id as employee_id, e. NAME as employeeName, s.money, sy.id as payout_id, sy.title as payoutTitle FROM salary s JOIN employee e ON s.employee_id=e.id join systemdictionaryitem sy on s.payout_id=sy.id
and e.name like concat('%',#{keyword},'%')
and s.money>=#{minSalary}
and s.money <=#{maxSalary}
and s.payout_id=#{payoutId}
